SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_emissao_tit_rec ( nr_titulo_p bigint, nr_seq_cobranca_p bigint, ie_evento_p text, nm_usuario_p text) AS $body$
DECLARE


ie_evento_w			varchar(3);
ie_emissao_bloqueto_w		varchar(3);
ie_tipo_emissao_titulo_w	varchar(5);
cd_estabelecimento_w		smallint;

c01 CURSOR FOR
	SELECT	ie_tipo_emissao_titulo
	from	regra_emissao_tit_rec
	where	ie_situacao 		= 'A'
	and	ie_evento		= ie_evento_p
	and	coalesce(ie_emissao_bloqueto, ie_emissao_bloqueto_w) = ie_emissao_bloqueto_w
	and	cd_estabelecimento	= cd_estabelecimento_w;


BEGIN

ie_emissao_bloqueto_w	:= 'X';

if (nr_seq_cobranca_p IS NOT NULL AND nr_seq_cobranca_p::text <> '') then

	select	coalesce(max(ie_emissao_bloqueto),0),
		coalesce(max(cd_estabelecimento),0)
	into STRICT	ie_emissao_bloqueto_w,
		cd_estabelecimento_w
	from	cobranca_escritural
	where	nr_sequencia	= nr_seq_cobranca_p;

elsif (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	select	coalesce(max(cd_estabelecimento),0)
	into STRICT	cd_estabelecimento_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_p;
end if;

open c01;
loop
fetch c01 into
	ie_tipo_emissao_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

end loop;
close c01;

if (ie_tipo_emissao_titulo_w IS NOT NULL AND ie_tipo_emissao_titulo_w::text <> '') then
	update	titulo_receber
	set	ie_tipo_emissao_titulo	= ie_tipo_emissao_titulo_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_titulo 		in (SELECT	coalesce(nr_titulo_p,0)
					
					
union

					SELECT	nr_titulo
					from	titulo_receber_cobr
					where	nr_seq_cobranca	= coalesce(nr_seq_cobranca_p,0));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
