SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_empresa_compl_pf ( cd_pessoa_fisica_p text, ie_tipo_complemento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_complemento_dest_w	bigint;
qt_registro_w			bigint;
cd_empresa_refer_w		bigint;

/* 1 - Residencial
   2 - Comercial */
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_tipo_complemento_p > 0) then

	if (ie_tipo_complemento_p = 1) then
		ie_tipo_complemento_dest_w := 2;
	elsif (ie_tipo_complemento_p = 2) then
		ie_tipo_complemento_dest_w := 1;
	end if;

	select	count(*)
	into STRICT	qt_registro_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	ie_tipo_complemento = ie_tipo_complemento_dest_w;

	if (qt_registro_w > 0) then

		select	cd_empresa_refer
		into STRICT	cd_empresa_refer_w
		from	compl_pessoa_fisica
		where	ie_tipo_complemento_p = ie_tipo_complemento
		and	cd_pessoa_fisica_p = cd_pessoa_fisica;
		update	compl_pessoa_fisica
		set	cd_empresa_refer = cd_empresa_refer_w,
			nm_usuario	 = nm_usuario_p,
			dt_atualizacao 	 = clock_timestamp()
		where	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	ie_tipo_complemento = ie_tipo_complemento_dest_w;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
