CREATE OR REPLACE PROCEDURE atualizar_endereco_completo ( nr_sequencia_p bigint, ie_commit_p text default 'S') AS $body$
DECLARE


ds_endereco_w	end_endereco.ds_endereco_completo%type;WITH RECURSIVE cte AS (


c01 CURSOR FOR
SELECT	nr_sequencia
from 	end_endereco WHERE nr_sequencia = nr_sequencia_p
  UNION ALL


c01 CURSOR FOR
SELECT	nr_sequencia
from 	end_endereco JOIN cte c ON (c.prior nr_sequencia = nr_seq_superior)

) SELECT * FROM cte;
;
c01_w	c01%rowtype;


BEGIN

open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ds_endereco_w :=  obter_endereco_completo(c01_w.nr_sequencia);

	if (coalesce(ds_endereco_w,'X') <> 'X') then
		update	end_endereco
		set	ds_endereco_completo = ds_endereco_w
		where	nr_sequencia = c01_w.nr_sequencia;
	end if;

	end;
end loop;
close c01;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
