SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_enfermeiro_resp ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, ds_justificativa_p text) AS $body$
DECLARE


ie_possui_enf_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);


BEGIN
select	coalesce(max('S'),'N')
into STRICT	ie_possui_enf_w
from	atend_enfermagem_resp
where	nr_atendimento = nr_atendimento_p;

if (coalesce(cd_pessoa_fisica_p::text, '') = '') then
	delete from atend_enfermagem_resp
	where	nr_sequencia = (SELECT	max(nr_sequencia)
							from	atend_enfermagem_resp
							where	nr_atendimento = nr_atendimento_p)
	and		nr_atendimento = nr_atendimento_p;
else
	if (ie_possui_enf_w = 'S') then
		select	coalesce(max(cd_pessoa_fisica),'0')
		into STRICT	cd_pessoa_fisica_w
		from	atend_enfermagem_resp
		where	nr_sequencia = (SELECT	max(nr_sequencia)
								from	atend_enfermagem_resp
								where	nr_atendimento = nr_atendimento_p)
		and		nr_atendimento = nr_atendimento_p;

		update	atend_enfermagem_resp
		set		dt_final_resp = clock_timestamp()
		where	nr_sequencia = (SELECT	max(nr_sequencia)
								from	atend_enfermagem_resp
								where	nr_atendimento = nr_atendimento_p)
		and		nr_atendimento = nr_atendimento_p;
	end if;

	insert into atend_enfermagem_resp(
						nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						cd_pessoa_fisica,
						nr_atendimento,
						cd_pessoa_resp_ant,
						dt_inicio_resp,
						ds_justificativa)
				SELECT	nextval('atend_enfermagem_resp_seq'),
						clock_timestamp(),
						nm_usuario_p,
						cd_pessoa_fisica_p,
						nr_atendimento_p,
						coalesce(cd_pessoa_fisica_w, null),
						clock_timestamp(),
						ds_justificativa_p
				;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
