CREATE OR REPLACE PROCEDURE atualizar_equipe_regulacao_js ( nr_seq_tipo_servico_p bigint, nr_seq_unidade_p bigint, nr_seq_equipe_p bigint, nr_sequencia_p bigint, nr_atendimento_novo_p INOUT bigint, ie_confirm_gerar_propaci_p text, ie_confirm_gerar_atend_p text, ie_gerar_atendimento_p text, cd_estabelecimento_p text, cd_usuario_conv_p text, cd_medico_regulador_p text, cd_pessoa_fisica_p text, ds_mensagem_gerar_evo_p INOUT text, ie_retorno_p INOUT text, nm_usuario_p text) AS $body$
DECLARE

 
nr_atendimento_novo_w	bigint;
ie_retorno_w		varchar(1);

BEGIN
 
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_retorno_w 
from	eme_reg_evolucao a, 
	eme_regulacao b 
where	a.nr_seq_reg = b.nr_sequencia 
and	b.nr_atendimento = coalesce(nr_atendimento_novo_p,0) 
and	not exists (	SELECT 1 
			from  evolucao_paciente x 
			where a.nr_sequencia = x.nr_seq_regulacao);
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	CALL atualiza_equipe_regulacao( 
		nr_sequencia_p, 
		nr_seq_equipe_p, 
		nm_usuario_p);
 
	if (ie_gerar_atendimento_p = 'E') then 
		begin 
		if (ie_confirm_gerar_atend_p = 'S') then 
			begin 
			nr_atendimento_novo_w := gerar_atendimento_regulacao( 
			nr_sequencia_p, cd_pessoa_fisica_p, cd_estabelecimento_p, cd_usuario_conv_p, cd_medico_regulador_p, nr_seq_tipo_servico_p, nr_seq_unidade_p, nm_usuario_p, nr_atendimento_novo_w);
			if (nr_atendimento_novo_w IS NOT NULL AND nr_atendimento_novo_w::text <> '') then 
				begin 
				ds_mensagem_gerar_evo_p := substr(obter_texto_tasy(106278, wheb_usuario_pck.get_nr_seq_idioma),1,255);
				end;
			end if;
			end;
		end if;
		if (ie_confirm_gerar_propaci_p = 'S') then 
			begin 
			CALL gerar_propaci_regulacao( 
				nr_sequencia_p, 
				nm_usuario_p);
			end;
		end if;
		end;
	end if;
	end;
end if;
ie_retorno_p		:= ie_retorno_w;
nr_atendimento_novo_p	:= nr_atendimento_novo_w;
commit;
end;
