SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_especialidade_proc ( nr_interno_conta_p bigint, nr_sequencia_p bigint) AS $body$
DECLARE



nr_sequencia_w		bigint;
nr_seq_proc_princ_w		bigint;
nr_cirurgia_w			bigint;
nr_prescricao_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_especialidade_w		bigint;
cd_especialidade_ww		bigint;

cd_procedimento_princ_w	bigint;
ie_origem_proced_princ_w	bigint;
ie_proc_mat_w			smallint;
cd_estabelecimento_w		bigint;
cd_medico_w			varchar(10);
cd_medico_atend_w		varchar(10);
cd_medico_executor_w		varchar(10);
cd_medico_prescr_w		varchar(10);
ie_forma_w			varchar(15);
ie_forma_especialidade_w	varchar(15);

c01 CURSOR FOR
SELECT 	1 ie_proc_mat,
	nr_sequencia,
	coalesce(nr_seq_proc_princ, 0),
	coalesce(nr_cirurgia, 0),
	coalesce(nr_prescricao,0),
	cd_procedimento,
	ie_origem_proced,
	coalesce(cd_medico_executor,'X')
from 	procedimento_paciente
where 	nr_sequencia		= nr_sequencia_p;

BEGIN

select	max(coalesce(a.cd_estabelecimento,b.cd_estabelecimento)),
	max(b.cd_medico_resp)
into STRICT	cd_estabelecimento_w,
	cd_medico_atend_w
from	atendimento_paciente b,
	conta_paciente a
where	a.nr_interno_conta	= nr_interno_conta_p
and	a.nr_atendimento	= b.nr_atendimento;

select	coalesce(max(ie_forma_especialidade),'MEMPMAPR')
into STRICT	ie_forma_especialidade_w
from	parametro_faturamento
where	cd_estabelecimento	= cd_estabelecimento_w;

select max(cd_especialidade)
into STRICT	cd_especialidade_ww
from	especialidade_medica
where	cd_especialidade 	=
	(SELECT CASE WHEN coalesce(vl_parametro::text, '') = '' THEN  vl_parametro_padrao  ELSE vl_parametro END
	from funcao_parametro
	where cd_funcao    = 0
	and nr_sequencia = 19);

OPEN C01;
LOOP
FETCH C01 into
	ie_proc_mat_w,
	nr_sequencia_w,
	nr_seq_proc_princ_w,
	nr_cirurgia_w,
	nr_prescricao_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	cd_medico_executor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	cd_procedimento_princ_w		:= 0;
	ie_origem_proced_princ_w		:= 0;
	cd_especialidade_w			:= 0;
	cd_medico_prescr_w			:= '';
	if (nr_prescricao_w > 0) then
		select	max(cd_medico)
		into STRICT	cd_medico_prescr_w
		from	prescr_medica
		where	nr_prescricao		= nr_prescricao_w;
	end if;
	if (nr_cirurgia_w <> 0) then
		select 	max(cd_procedimento_princ),
				max(ie_origem_proced)
		into STRICT		cd_procedimento_princ_w,
				ie_origem_proced_princ_w
		from cirurgia
		where nr_cirurgia = nr_cirurgia_w;
	end if;
	if (nr_seq_proc_princ_w <> 0) then
		select 	max(cd_procedimento),
				max(ie_origem_proced)
		into STRICT		cd_procedimento_princ_w,
				ie_origem_proced_princ_w
		from procedimento_paciente
		where nr_sequencia = nr_seq_proc_princ_w;
	end if;
	if (cd_procedimento_Princ_w <> 0) then
		cd_procedimento_w 	:= cd_procedimento_princ_w;
		ie_origem_proced_w 	:= ie_origem_proced_princ_w;
	end if;

	cd_especialidade_w		:= 0;
	ie_forma_w			:= ie_forma_especialidade_w;
	FOR i IN 1.. 4 LOOP
		if (cd_especialidade_w = 0) then
			if (substr(ie_forma_w,1,2) = 'PR') then
				if (cd_procedimento_w <> 0) then
					select coalesce(cd_especialidade_medica,0)
					into STRICT	cd_especialidade_w
					from	estrutura_procedimento_v
					where	cd_procedimento 	= cd_procedimento_w
					and	ie_origem_proced 	= ie_origem_proced_w;
				end if;
			elsif (substr(ie_forma_w,1,2) = 'ME') and (cd_medico_executor_w IS NOT NULL AND cd_medico_executor_w::text <> '') then
				select coalesce(obter_especialidade_medico(cd_medico_executor_w, 'V'),0)
				into STRICT cd_especialidade_w
				;
			elsif (substr(ie_forma_w,1,2) = 'MP') and (cd_medico_prescr_w IS NOT NULL AND cd_medico_prescr_w::text <> '') then
				select coalesce(obter_especialidade_medico(cd_medico_prescr_w, 'V'),0)
				into STRICT cd_especialidade_w
				;
			elsif (substr(ie_forma_w,1,2) = 'MA') and (cd_medico_atend_w IS NOT NULL AND cd_medico_atend_w::text <> '') then
				select coalesce(obter_especialidade_medico(cd_medico_atend_w, 'V'),0)
				into STRICT cd_especialidade_w
				;
			end if;
			ie_forma_w		:= substr(ie_forma_w,3,15);
		end if;
	END LOOP;
	if (cd_especialidade_w = 0) then
		cd_especialidade_w	:= coalesce(cd_especialidade_ww,0);
	end if;
	if (cd_especialidade_w <> 0) then
		if (ie_proc_mat_w = 1) then
			update procedimento_paciente
			set cd_especialidade = cd_especialidade_w
			where nr_sequencia = nr_sequencia_w;
		else
			update Material_atend_paciente
			set cd_especialidade = cd_especialidade_w
			where nr_sequencia = nr_sequencia_w;
		end if;
	end if;
END LOOP;
CLOSE C01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_especialidade_proc ( nr_interno_conta_p bigint, nr_sequencia_p bigint) FROM PUBLIC;

