SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_espec_medic_conta ( nr_interno_conta_p bigint) AS $body$
DECLARE



nr_sequencia_w			bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_estabelecimento_w		bigint;
cd_convenio_w			bigint;
cd_especialidade_w		bigint;
ie_funcao_medico_w		varchar(10);
ie_atualiza_espec_medico_w	varchar(01);
ie_atualiza_espec_medico_ww	varchar(01);
nr_atendimento_w		bigint;
ie_tipo_atendimento_w		smallint;

c01 CURSOR FOR
SELECT  nr_sequencia,
	cd_procedimento,
	ie_origem_proced
from 	procedimento_paciente
where 	nr_interno_conta 	= nr_interno_conta_p
and	ie_atualiza_espec_medico_w = 'S'
and	ie_atualiza_espec_medico_ww = 'S'
and	coalesce(cd_especialidade::text, '') = '';


BEGIN

select	max(cd_estabelecimento),
	max(cd_convenio_parametro)
into STRICT	cd_estabelecimento_w,
	cd_convenio_w
from	conta_paciente
where	nr_interno_conta	= nr_interno_conta_p;

select	coalesce(max(ie_atualiza_espec_medica),'N')
into STRICT	ie_atualiza_espec_medico_w
from	parametro_faturamento
where	cd_estabelecimento	= cd_estabelecimento_w;

select	coalesce(max(ie_atualiza_func_medico),'N')
into STRICT	ie_atualiza_espec_medico_ww
from	convenio_estabelecimento
where	cd_convenio		= cd_convenio_w
and	cd_estabelecimento	= cd_estabelecimento_w;

select	nr_atendimento
into STRICT	nr_atendimento_w
from	conta_paciente
where	nr_interno_conta = nr_interno_conta_p;

select	ie_tipo_atendimento
into STRICT	ie_tipo_atendimento_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_w;

OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w,
	cd_procedimento_w,
	ie_origem_proced_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	SELECT * FROM obter_proced_espec_medica(cd_estabelecimento_w, cd_convenio_w, cd_procedimento_w, ie_origem_proced_w, null, null, null, null, null, cd_especialidade_w, ie_funcao_medico_w, null, null, ie_tipo_atendimento_w) INTO STRICT cd_especialidade_w, ie_funcao_medico_w;

	if (cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') then
		update	procedimento_paciente
		set	cd_especialidade	= cd_especialidade_w
		where	nr_sequencia 		= nr_sequencia_w;
	end if;
END LOOP;
CLOSE C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
