CREATE OR REPLACE PROCEDURE atualizar_espec_med_eup_agenda (nr_atendimento_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_medico_eup_w		varchar(10);
cd_medico_agenda_w	varchar(10);
cd_especialidade_w	integer;
ie_esp_med_w		varchar(1);
nr_seq_medico_w		bigint;


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then 
 
	select	max(cd_medico_resp) 
	into STRICT	cd_medico_eup_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	select	max(b.cd_pessoa_fisica), 
		max(b.cd_especialidade) 
	into STRICT	cd_medico_agenda_w, 
		cd_especialidade_w 
	from	agenda b, 
		agenda_consulta a 
	where	b.cd_agenda	= a.cd_agenda 
	and	a.nr_sequencia	= nr_seq_agenda_p 
	and	a.nr_atendimento	= nr_atendimento_p;
 
	ie_esp_med_w := obter_se_especialidade_medico(cd_medico_agenda_w, cd_especialidade_w);
 
	if (cd_medico_eup_w = cd_medico_agenda_w) and (ie_esp_med_w = 'S') then 
 
		select	coalesce(max(nr_sequencia),0) 
		into STRICT	nr_seq_medico_w 
		from	atendimento_troca_medico 
		where	nr_atendimento	= nr_atendimento_p 
		and	cd_medico_atual	= cd_medico_eup_w;
 
		update	atendimento_troca_medico 
		set	cd_especialidade	= cd_especialidade_w 
		where	nr_sequencia	= nr_seq_medico_w;
 
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
