CREATE OR REPLACE PROCEDURE atualizar_estagio_autoriz_conv (nr_seq_autor_cir_p bigint, ie_estagio_autor_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_autor_conv_w	autorizacao_convenio.nr_sequencia%type;
nr_seq_estagio_w		estagio_autorizacao.nr_sequencia%type;
ie_atualiza_autor_conv_w	varchar(40);


BEGIN

ie_atualiza_autor_conv_w := Obter_Param_Usuario(3006, 26, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_atualiza_autor_conv_w);

if	(ie_estagio_autor_p = '9' AND ie_atualiza_autor_conv_w = 'S') then
	begin

	select	coalesce(nr_seq_autor_conv,0)
	into STRICT	nr_seq_autor_conv_w
	from	autorizacao_cirurgia
	where	nr_sequencia = nr_seq_autor_cir_p;

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_estagio_w
	from	estagio_autorizacao
	where	ie_interno = '5'
	and	ie_situacao = 'A'
	and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;

	if (nr_seq_autor_conv_w = 0) then

		if (nr_seq_estagio_w <> 0) then

			begin
			select	coalesce(nr_sequencia,0)
			into STRICT	nr_seq_autor_conv_w
			from	autorizacao_convenio
			where	nr_seq_estagio <> nr_seq_estagio_w
			and	nr_seq_autor_cirurgia = nr_seq_autor_cir_p;

			exception
			when others then
				nr_seq_autor_conv_w := 0;
			end;

		end if;
	end if;

	if (nr_seq_autor_conv_w <> 0) and (nr_seq_estagio_w <> 0) then
		begin

		CALL atualizar_autorizacao_convenio(nr_seq_autor_conv_w, nm_usuario_p, nr_seq_estagio_w, 'N', 'N', 'S');

		end;
	end if;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
