CREATE OR REPLACE PROCEDURE atualizar_estagio_autor_mat ( nr_seq_agenda_p bigint, ie_estagio_autor_p text, nm_usuario_p text) AS $body$
DECLARE

nr_seq_autor_w		bigint;
nr_atendimento_w	bigint;
nr_seq_autorizacao_w	bigint;
ds_estagio_w		varchar(255);
nr_seq_estagio_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_atendimento,
		nr_seq_estagio
	from	autorizacao_convenio
	where	nr_seq_agenda = nr_seq_agenda_p;


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) and (ie_estagio_autor_p IS NOT NULL AND ie_estagio_autor_p::text <> '')then
	begin

	open C01;
	loop
	fetch C01 into
		nr_seq_autor_w,
		nr_atendimento_w,
		nr_seq_estagio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(nr_seq_autor_w,0) > 0) then
			CALL atualizar_autorizacao_convenio(nr_seq_autor_w,nm_usuario_p, ie_estagio_autor_p,'N','N','S');

			select	max(ds_estagio)
			into STRICT	ds_estagio_w
			from	estagio_autorizacao
			where	nr_sequencia = ie_estagio_autor_p
			and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;

			if (ds_estagio_w IS NOT NULL AND ds_estagio_w::text <> '') and (nr_seq_estagio_w <> (ie_estagio_autor_p)::numeric )then

				insert into autorizacao_convenio_hist(nr_sequencia,
					 dt_atualizacao,
					 nm_usuario,
					 nr_atendimento,
					 nr_seq_autorizacao,
					 ds_historico,
					 nr_sequencia_autor)
				values (nextval('autorizacao_convenio_hist_seq'),
					 clock_timestamp(),
					 nm_usuario_p,
					 nr_atendimento_w,
					 nr_seq_autorizacao_w,
					 wheb_mensagem_pck.get_Texto(311638,'DS_ESTAGIO_W='||DS_ESTAGIO_W),
					 nr_seq_autor_w);
			end if;
		end if;
		end;
	end loop;
	close C01;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
