CREATE OR REPLACE PROCEDURE atualizar_estagio_ordem_serv ( nr_seq_estagio_p bigint, cd_funcao_p bigint, nr_sequencia_p bigint) AS $body$
BEGIN
if (nr_seq_estagio_p IS NOT NULL AND nr_seq_estagio_p::text <> '') and (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	update 	man_ordem_servico
	set 	nr_seq_estagio = nr_seq_estagio_p,
		cd_funcao = CASE WHEN coalesce(cd_funcao_p,0)=0 THEN  cd_funcao  ELSE cd_funcao_p END ,
		nm_usuario = 'WebService'
	where  	nr_sequencia = nr_sequencia_p;

	update  	man_ordem_servico_exec
	set     	dt_ult_visao  = NULL
	where   	nr_seq_ordem = nr_sequencia_p;
	commit;
end if;
end;
$body$
