SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_executor_servico (nr_seq_agenda_p bigint, cd_executor_p text, nm_usuario_p text) AS $body$
DECLARE


nr_prescricao_w	bigint;
qt_proced_prescr_w	bigint;
qt_proced_conta_w	bigint;


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (cd_executor_p IS NOT NULL AND cd_executor_p::text <> '') then
	/* consistir e atualizar executor agenda */

	update	agenda_consulta
	set	cd_medico	= cd_executor_p,
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_agenda_p
	and	((cd_medico	<> cd_executor_p) or (coalesce(cd_medico::text, '') = ''));

	/* obter prescrição */

	select	coalesce(max(nr_prescricao),0)
	into STRICT	nr_prescricao_w
	from	prescr_medica
	where	nr_seq_agecons = nr_seq_agenda_p;

	if (coalesce(nr_prescricao_w,0) > 0) then
		/* consistir executor prescrição */

		select	coalesce(count(*),0)
		into STRICT	qt_proced_prescr_w
		from	prescr_procedimento
		where	nr_prescricao		= nr_prescricao_w
		and	((cd_medico_exec	<> cd_executor_p) or (coalesce(cd_medico_exec::text, '') = ''));

		/* atualizar executor prescrição */

		if (coalesce(qt_proced_prescr_w,0) > 0) then
			update	prescr_procedimento
			set	cd_medico_exec	= cd_executor_p,
				nm_usuario		= nm_usuario_p
			where	nr_prescricao		= nr_prescricao_w
			and	((cd_medico_exec	<> cd_executor_p) or (coalesce(cd_medico_exec::text, '') = ''));
		end if;

		/* consistir executor conta */

		select	coalesce(count(*),0)
		into STRICT	qt_proced_conta_w
		from	procedimento_paciente
		where	nr_prescricao		= nr_prescricao_w
		and	((cd_medico_executor	<> cd_executor_p) or (coalesce(cd_medico_executor::text, '') = ''));

		/* atualizar executor conta */

		if (coalesce(qt_proced_conta_w,0) > 0) then
			update	procedimento_paciente
			set	cd_medico_executor	= cd_executor_p,
				nm_usuario		= nm_usuario_p
			where	nr_prescricao		= nr_prescricao_w
			and	((cd_medico_executor	<> cd_executor_p) or (coalesce(cd_medico_executor::text, '') = ''));
		end if;
	end if;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
