CREATE OR REPLACE PROCEDURE atualizar_fator_rh_pf (cd_pessoa_fisica_p text, ie_exame_pac_p text, ds_resultado_p text, nm_usuario_p text) AS $body$
DECLARE


ds_resultado_w	varchar(255);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ds_resultado_p IS NOT NULL AND ds_resultado_p::text <> '') then

	if (upper(ds_resultado_p) = 'POSITIVO') or (ds_resultado_p = '+') then
		ds_resultado_w := '+';
	elsif (upper(ds_resultado_p) = 'NEGATIVO') or (ds_resultado_p = '-') then
		ds_resultado_w := '-';
	elsif (upper(ds_resultado_p) = 'A') then
		ds_resultado_w := 'A';
	elsif (upper(ds_resultado_p) = 'B') then
		ds_resultado_w := 'B';
	elsif (upper(ds_resultado_p) = 'AB') then
		ds_resultado_w := 'AB';
	elsif (upper(ds_resultado_p) = 'O') then
		ds_resultado_w := 'O';
	end if;

	if (ie_exame_pac_p = 'GS') then
		update 	pessoa_fisica
		set		ie_tipo_sangue = ds_resultado_w
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	elsif (ie_exame_pac_p = 'FR') then
		update 	pessoa_fisica
		set		ie_fator_rh = ds_resultado_w
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
