CREATE OR REPLACE PROCEDURE atualizar_final_vig_brasindice ( nm_usuario_p text, nr_sequencia_p bigint, dt_final_vigencia_p timestamp) AS $body$
DECLARE



ds_erro_w varchar(255);


BEGIN


begin
	update	material_brasindice
	set	dt_final_vigencia 	= fim_dia(dt_final_vigencia_p),
		dt_atualizacao 		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_sequencia_p
	and	coalesce(dt_final_vigencia::text, '') = '';
exception
	when others then
	ds_erro_w := substr(wheb_mensagem_pck.get_texto(305079),1,255);
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
