CREATE OR REPLACE PROCEDURE atualizar_final_vig_simpro ( nm_usuario_p text, cd_simpro_p bigint, cd_material_p bigint, cd_convenio_p bigint, cd_estabelecimento_p bigint, dt_final_vigencia_p timestamp) AS $body$
DECLARE


ds_erro_w	varchar(255);


BEGIN


begin
	update	material_simpro
	set	dt_final_vigencia 	= dt_final_vigencia_p,
		dt_atualizacao 		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	cd_simpro		= cd_simpro_p
	and	cd_material		= cd_material_p
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p) 	= cd_estabelecimento_p
	and	coalesce(cd_convenio,cd_convenio_p) 			= cd_convenio_p
	and	coalesce(dt_final_vigencia::text, '') = '';
exception
	when others then
	ds_erro_w:= wheb_mensagem_pck.get_texto(796846,'CD_MATERIAL_P=' || cd_material_p);
end;

commit;

end;
$body$
