SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_funcao_medic_conta ( nr_interno_conta_p bigint) AS $body$
DECLARE



nr_sequencia_w			bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_estabelecimento_w		bigint;
cd_convenio_w			bigint;
cd_especialidade_w		bigint;
ie_funcao_medico_w		varchar(10);
IE_ATUALIZA_FUNC_MEDICO_w	varchar(01);
ie_tipo_atendimento_w		smallint;
nr_atendimento_w		bigint;

c01 CURSOR FOR
SELECT  nr_sequencia,
	cd_procedimento,
	ie_origem_proced
from 	procedimento_paciente
where 	nr_interno_conta 	= nr_interno_conta_p
and	coalesce(ie_funcao_medico,'0') = '0'
and	IE_ATUALIZA_FUNC_MEDICO_w = 'S';


BEGIN

select	max(cd_estabelecimento),
	max(cd_convenio_parametro),
	max(nr_atendimento)
into STRICT	cd_estabelecimento_w,
	cd_convenio_w,
	nr_atendimento_w
from	conta_paciente
where	nr_interno_conta	= nr_interno_conta_p;

select	max(IE_ATUALIZA_FUNC_MEDICO)
into STRICT	IE_ATUALIZA_FUNC_MEDICO_w
from	parametro_faturamento
where	cd_estabelecimento	= cd_estabelecimento_w;

select	coalesce(max(IE_ATUALIZA_FUNC_MEDICO), IE_ATUALIZA_FUNC_MEDICO_w)
into STRICT	IE_ATUALIZA_FUNC_MEDICO_w
from	convenio_estabelecimento
where	cd_convenio		= cd_convenio_w
and	cd_estabelecimento	= cd_estabelecimento_w;

select	ie_tipo_atendimento
into STRICT	ie_tipo_atendimento_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_w;


OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w,
	cd_procedimento_w,
	ie_origem_proced_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	SELECT * FROM obter_proced_espec_medica(cd_estabelecimento_w, cd_convenio_w, cd_procedimento_w, ie_origem_proced_w, null, null, null, null, null, cd_especialidade_w, ie_funcao_medico_w, null, null, ie_tipo_atendimento_w) INTO STRICT cd_especialidade_w, ie_funcao_medico_w;

	if (ie_funcao_medico_w IS NOT NULL AND ie_funcao_medico_w::text <> '') then
		update	procedimento_paciente
		set	ie_funcao_medico	= ie_funcao_medico_w
		where	nr_sequencia 		= nr_sequencia_w;
	end if;
END LOOP;
CLOSE C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
