CREATE OR REPLACE PROCEDURE atualizar_gera_os_npi (nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_superior_w	bigint;

/*
ie_opcao_p
Novo Registro = 'N'
Alteração superior = 'A'
*/
BEGIN

if (ie_opcao_p = 'N') then

	select	coalesce(max(nr_seq_superior),0)
	into STRICT	nr_seq_superior_w
	from	proj_cron_etapa a
	where	a.nr_sequencia = nr_sequencia_p;

	update	proj_cron_etapa
	set	ie_gera_os = 'N',
		ie_status_etapa = '1'
	where	nr_sequencia = nr_seq_superior_w;

elsif (ie_opcao_p = 'A') then

	update	proj_cron_etapa
	set	ie_gera_os = 'N',
		ie_status_etapa = '1'
	where	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
