CREATE OR REPLACE PROCEDURE atualizar_glosa_post_item (nr_seq_ret_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_amenor_post_w	double precision;
vl_glosado_post_w	double precision;
vl_pago_digitado_w	double precision;


BEGIN

select	coalesce(sum(vl_glosa),0)
into STRICT	vl_amenor_post_w
from	convenio_retorno_glosa a,
	motivo_glosa b
where	a.cd_motivo_glosa				= b.cd_motivo_glosa
and	a.nr_seq_ret_item				= nr_seq_ret_item_p
and	coalesce(coalesce(a.ie_acao_glosa, b.ie_acao_glosa),'R') 	= 'R';

select	coalesce(sum(vl_glosa),0)
into STRICT	vl_glosado_post_w
from	convenio_retorno_glosa a,
	motivo_glosa b
where	a.cd_motivo_glosa				= b.cd_motivo_glosa
and	a.nr_seq_ret_item				= nr_seq_ret_item_p
and	coalesce(coalesce(a.ie_acao_glosa, b.ie_acao_glosa),'R') 	= 'A';

select	coalesce(sum(vl_pago_digitado),0)
into STRICT	vl_pago_digitado_w
from	convenio_retorno_glosa a,
	motivo_glosa b
where	a.cd_motivo_glosa				= b.cd_motivo_glosa
and	a.nr_seq_ret_item				= nr_seq_ret_item_p;

update	convenio_retorno_item
set	vl_amenor_post	= vl_amenor_post_w,
	vl_glosado_post	= vl_glosado_post_w,
	vl_pago		= vl_pago_digitado_w
where	nr_sequencia	= nr_seq_ret_item_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
