CREATE OR REPLACE PROCEDURE atualizar_grupo_prod_financ (nr_adiantamento_p bigint, nr_titulo_receber_p bigint, nr_seq_movto_cartao_p bigint, nr_seq_cheque_p bigint, nm_usuario_p text, nr_seq_produto_p bigint) AS $body$
DECLARE


nr_seq_grupo_prod_w	bigint;
nr_seq_subgrupo_prod_w	bigint;


BEGIN

if (nr_seq_produto_p IS NOT NULL AND nr_seq_produto_p::text <> '') then

	select	max(nr_seq_grupo)
	into STRICT	nr_seq_subgrupo_prod_w
	from	produto_financeiro
	where	nr_sequencia	= nr_seq_produto_p;

	select	max(nr_seq_grupo_sup)
	into STRICT	nr_seq_grupo_prod_w
	from	grupo_prod_financ
	where	nr_sequencia	= nr_seq_subgrupo_prod_w;

	if (nr_adiantamento_p IS NOT NULL AND nr_adiantamento_p::text <> '') then
		update	adiantamento
		set	nr_seq_grupo_prod	= nr_seq_grupo_prod_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_adiantamento		= nr_adiantamento_p;

	elsif (nr_titulo_receber_p IS NOT NULL AND nr_titulo_receber_p::text <> '') then
		update	titulo_receber
		set	nr_seq_grupo_prod	= nr_seq_grupo_prod_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_titulo		= nr_titulo_receber_p;

	elsif (nr_seq_movto_cartao_p IS NOT NULL AND nr_seq_movto_cartao_p::text <> '') then
		update	movto_cartao_cr
		set	nr_seq_grupo_prod	= nr_seq_grupo_prod_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_seq_movto_cartao_p;

	elsif (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') then
		update	cheque_cr
		set	nr_seq_grupo_prod	= nr_seq_grupo_prod_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_seq_cheque		= nr_seq_cheque_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
