SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_guia_conta_autor (nr_atendimento_p bigint, cd_convenio_p bigint, cd_autorizacao_p text) AS $body$
DECLARE


qt_proc_conta_w	bigint;
qt_mat_conta_w	bigint;


BEGIN

select 	count(*)
into STRICT 	qt_proc_conta_w
from 	procedimento_paciente a
where	a.nr_atendimento	= nr_atendimento_p
and 	a.cd_convenio 		= cd_convenio_p;

select 	count(*)
into STRICT 	qt_mat_conta_w
from 	material_atend_paciente a
where	a.nr_atendimento	= nr_atendimento_p
and 	a.cd_convenio 		= cd_convenio_p;

if (qt_proc_conta_w > 0) then
	update	procedimento_paciente x
	set 	x.nr_doc_convenio	= coalesce(cd_autorizacao_p, x.nr_doc_convenio)
	where	x.nr_atendimento	= nr_atendimento_p
	and 	x.nr_sequencia		in (SELECT	a.nr_sequencia
					   from 	procedimento_paciente a
					   where	a.nr_atendimento	= nr_atendimento_p
					   and		a.cd_convenio		= cd_convenio_p);
end if;

if (qt_mat_conta_w > 0) then
	update	material_atend_paciente x
	set	x.nr_doc_convenio	= coalesce(cd_autorizacao_p, x.nr_doc_convenio)
	where	x.nr_atendimento	= nr_atendimento_p
	and 	x.nr_sequencia		in (SELECT	a.nr_sequencia
					    from 	material_atend_paciente a
					    where	a.nr_atendimento 	= nr_atendimento_p
					    and		a.cd_convenio		= cd_convenio_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
