CREATE OR REPLACE PROCEDURE atualizar_guia_retorno ( nr_seq_retorno_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_interno_conta_w	bigint;
cd_autorizacao_w	varchar(20);
vl_pago_w		double precision;

C01 CURSOR FOR
	SELECT	nr_interno_conta,
		cd_autorizacao,
		vl_pago
	from convenio_retorno_item
	where nr_seq_retorno = nr_seq_retorno_p;


BEGIN

open c01;
loop
	fetch c01 into	nr_interno_conta_w,
			cd_autorizacao_w,
			vl_pago_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	update conta_paciente_guia
	set vl_convenio = coalesce(vl_convenio,0) + vl_pago_w
	where nr_interno_conta	= nr_interno_conta_w
	  and cd_autorizacao	= cd_autorizacao_w
	  and coalesce(dt_convenio::text, '') = '';
end loop;
close c01;
/* commit; Bruna, 0603/2007 - Comentei pois estava dando problema no retorno	*/

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
