SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_hc_resp_serv_pac ( nr_sequencia_p bigint, ie_controle_p text, nm_usuario_p text, nr_seq_responsavel_p bigint) AS $body$
DECLARE

		
ds_operacao_w 		varchar(1);


BEGIN
if (ie_controle_p IS NOT NULL AND ie_controle_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '' AND nr_seq_responsavel_p IS NOT NULL AND nr_seq_responsavel_p::text <> '') then
		begin
		update	hc_resp_servico_paciente c
		set	c.ie_controle	= ie_controle_p,
			c.nm_usuario	= nm_usuario_p,
			c.dt_atualizacao	= clock_timestamp()
		where (c.ie_controle	<> ie_controle_p
		or coalesce(c.ie_controle::text, '') = '')
		and	c.nr_sequencia	= nr_sequencia_p
        and c.cd_pessoa_fisica = (SELECT	b.cd_pessoa_fisica
                                from hc_resp_servico_paciente a, hc_profissional b
                                where	b.cd_pessoa_fisica 	= a.cd_pessoa_fisica
                                and (a.ie_controle	<> ie_controle_p 
								or  coalesce(a.ie_controle::text, '') = '')
                                and	a.nr_sequencia	= nr_sequencia_p
                                and	b.NR_SEQUENCIA	= nr_seq_responsavel_p);
		end;
	else
		begin
		update	hc_resp_servico_paciente
		set	ie_controle	= ie_controle_p,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	ie_controle	<> ie_controle_p;
		end;
	end if;
	if (ie_controle_p = 'S') then
		ds_operacao_w := 'I';
	else
		ds_operacao_w := 'E';
	end if;
	CALL hc_resp_serv_pac_controle_sp(nr_sequencia_p, ds_operacao_w, nm_usuario_p);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
