SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_horarios_agrup_medic ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_agrupamento_p bigint, nr_agrup_ant_p bigint, ie_farm_enf_p text, ie_volta_horario_composto_p text, ds_horario_composto_p text, ds_horarios_p INOUT text, cd_estabelecimento_p bigint, ie_volta_horario_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_menor_hora_w		varchar(255);
ds_horarios_w		varchar(255);
ds_primeira_hora_w	varchar(255);
nr_sequencia_w		bigint;
nr_seq_menor_horario_w	bigint;
nr_agrupamento_ant_w	double precision := -1;
NR_AGRUPAMENTO_W	double precision;
ds_prim_horario_w	varchar(5);
dt_primeiro_horario_w	timestamp;
dt_prescricao_w		timestamp;
qt_minutos_adic_w	bigint;
qt_hora_aplicacao_w	bigint;
cd_material_w		bigint;
nr_ocorrencia_w		double precision;
cd_intervalo_ww		varchar(10);
ds_horarios2_w		varchar(255);
ie_gerar_horarios_w	varchar(1);
hr_prim_horario_w	varchar(10);
hr_prim_horario_ant_w	varchar(10);
qt_min_aplicacao_w	bigint;

c01 CURSOR FOR 
SELECT	a.ds_horarios, 
	a.nr_sequencia 
from	prescr_material a 
where	a.nr_prescricao = nr_prescricao_p 
and	nr_agrupamento	= nr_agrupamento_p 
and	(a.ds_horarios IS NOT NULL AND a.ds_horarios::text <> '') 
and	obter_classif_material_proced(a.cd_material, null,null) <> 1 
and	coalesce(a.nr_sequencia_solucao::text, '') = '' 
and	coalesce(a.nr_sequencia_proc::text, '') = '' 
and	a.ie_agrupador		= 1 
and	a.ie_suspenso		<> 'S' 
and	coalesce(ie_administrar,'S')	= 'S' 
and	coalesce(a.nr_sequencia_diluicao::text, '') = '';

c02 CURSOR FOR 
SELECT	b.nr_sequencia, 
	b.nr_agrupamento, 
	b.qt_hora_aplicacao, 
	b.qt_min_aplicacao, 
	b.cd_intervalo, 
	b.nr_ocorrencia, 
	b.cd_material, 
	a.dt_prescricao, 
	b.hr_prim_horario 
from	prescr_material b, 
	prescr_medica a 
where	a.nr_prescricao	= nr_prescricao_p 
and	a.nr_prescricao	= b.nr_prescricao 
--and	b.nr_agrupamento = nr_agrupamento_p 
and	b.ie_agrupador	= 1 
order by b.nr_agrupamento;


BEGIN 
 
qt_minutos_adic_w := obter_param_usuario(3130, 67, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, qt_minutos_adic_w);
ie_gerar_horarios_w := obter_param_usuario(281, 423, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_gerar_horarios_w);
 
ds_menor_hora_w	:= '23:59';
open c01;
loop 
fetch c01 into 
	ds_horarios_w, 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	ds_primeira_hora_w	:= substr(ds_horarios_w,1,5);
	if (position(':' in substr(ds_horarios_w,1,3)) = 0) then 
		ds_primeira_hora_w	:= substr(ds_horarios_w,1,2) || ':00';
	end if;
	 
	if (ds_primeira_hora_w	< ds_menor_hora_w) then 
		ds_menor_hora_w		:= ds_primeira_hora_w;
		nr_seq_menor_horario_w	:= nr_sequencia_w;
	end if;	
end loop;
close c01;
 
select	dt_primeiro_horario, 
	dt_prescricao 
into STRICT	dt_primeiro_horario_w, 
	dt_prescricao_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
if (dt_primeiro_horario_w < clock_timestamp()) then 
	dt_primeiro_horario_w	:= clock_timestamp();
	dt_prescricao_w		:= clock_timestamp();
end if;
 
if (ie_farm_enf_p	= 'F') then 
 
	if (coalesce(nr_seq_menor_horario_w,0) <> 0) and (ie_volta_horario_p	= 'N') then 
		select	max(ds_horarios) 
		into STRICT	ds_horarios_w 
		from	prescr_material 
		where	nr_prescricao	= nr_prescricao_p 
		and	nr_sequencia	= nr_seq_menor_horario_w;
		 
		if (coalesce(ds_horario_composto_p,'X') <> 'X') then --Voltar todos os itens ao horário original. 
			ds_horarios_w	:= ds_horario_composto_p;
		end if;
		 
		update	prescr_material 
		set	ds_horarios	= ds_horarios_w, 
			nm_usuario	= nm_usuario_p, 
			dt_atualizacao	= clock_timestamp() 
		where	nr_prescricao	= nr_prescricao_p 
		and	nr_agrupamento	= nr_agrupamento_p 
		and	nr_sequencia	<> nr_seq_menor_horario_w 
		and	obter_classif_material_proced(cd_material, null,null) <> 1 
		and	coalesce(nr_sequencia_solucao::text, '') = '' 
		and	coalesce(nr_sequencia_proc::text, '') = '' 
		and	ie_agrupador		= 1 
		and	ie_suspenso		<> 'S' 
		and	coalesce(ie_administrar,'S')	= 'S' 
		and	coalesce(nr_sequencia_diluicao::text, '') = '';
 
		ds_horarios_p	:= ds_horarios_w;
		 
		commit;
	else	 
	 
		open c02;
		loop 
		fetch c02 into	 
			nr_sequencia_w, 
			nr_agrupamento_w, 
			qt_hora_aplicacao_w, 
			qt_min_aplicacao_w, 
			cd_intervalo_ww, 
			nr_ocorrencia_w, 
			cd_material_w, 
			dt_prescricao_w, 
			hr_prim_horario_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin 
			 
			if (nr_agrupamento_w <> nr_agrupamento_ant_w) then 
				begin 
				select	to_date(to_char(dt_prescricao_w,'dd/mm/yyyy') || coalesce(hr_prim_horario_ant_w, hr_prim_horario_w),'dd/mm/yyyy hh24:mi') 
				into STRICT	dt_primeiro_horario_w 
				;
				exception when others then 
					dt_primeiro_horario_w	:= dt_prescricao_w + (qt_minutos_adic_w/1440);
				end;				
				dt_primeiro_horario_w	:= dt_primeiro_horario_w + dividir((coalesce(qt_hora_aplicacao_w,0) * 60) + coalesce(qt_min_aplicacao_w,0),1440);
				hr_prim_horario_ant_w	:= to_char(dt_primeiro_horario_w,'hh24:mi');
			end if;
			 
			if (nr_agrupamento_w <> nr_agrupamento_ant_w) then 
				nr_ocorrencia_w := 0;
				SELECT * FROM calcular_horario_prescricao(NR_PRESCRICAO_p, cd_intervalo_ww, coalesce(dt_primeiro_horario_w,dt_prescricao_w), dt_primeiro_horario_w, 24, cd_material_w, 0, 0, nr_ocorrencia_w, ds_horarios_w, ds_horarios2_w, 'N', null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios2_w;
 
				ds_horarios_w		:= ds_horarios_w || ds_horarios2_w;
				ds_prim_horario_w	:= to_char(dt_primeiro_horario_w,'hh24:mi');
				 
				update	prescr_material 
				set	ds_horarios	= ds_horarios_w, 
					hr_prim_horario	= ds_prim_horario_w, 
					nr_ocorrencia	= nr_ocorrencia_w 
				where	nr_prescricao	= NR_PRESCRICAO_p 
				and	nr_agrupamento	= nr_agrupamento_p 
				and	nr_sequencia	= nr_sequencia_w;
				 
				--dt_primeiro_horario_w	:= dt_primeiro_horario_w + dividir((nvl(qt_hora_aplicacao_w,0) * 60) + nvl(qt_min_aplicacao_w,0),1440); 
				 
			else 
				update	prescr_material 
				set	ds_horarios	= ds_horarios_w, 
					hr_prim_horario	= ds_prim_horario_w, 
					nr_ocorrencia	= nr_ocorrencia_w 
				where	nr_prescricao	= nr_prescricao_p 
				and	nr_agrupamento	= nr_agrupamento_p 
				and	nr_sequencia	= nr_sequencia_w;
			end if;
			 
			nr_agrupamento_ant_w	:= nr_agrupamento_w;
			 
			end;
		end loop;
		close c02;
		 
/*		select	max(ds_horarios) 
		into	ds_horarios_w 
		from	prescr_material 
		where	nr_prescricao	= nr_prescricao_p 
		and	nr_sequencia	= nr_seq_menor_horario_w; 
		 
		update	prescr_material 
		set	ds_horarios	= ds_horarios_w, 
			nm_usuario	= nm_usuario_p, 
			dt_atualizacao	= sysdate 
		where	nr_prescricao	= nr_prescricao_p 
		and	nr_agrupamento	= nr_agrupamento_p 
		and	nr_sequencia	<> nr_seq_menor_horario_w 
		and	obter_classif_material_proced(cd_material, null,null) <> 1 
		and	nr_sequencia_solucao	is null 
		and	nr_sequencia_proc	is null 
		and	ie_agrupador		= 1 
		and	ie_suspenso		<> 'S' 
		and	nvl(ie_administrar,'S')	= 'S' 
		and	nr_sequencia_diluicao	is null; 
*/
 
 
	end if;
 
elsif (ie_farm_enf_p	= 'E') then 
 
	if (ie_volta_horario_composto_p	= 'S') then --Voltar todos os itens ao horário original. 
		nr_seq_menor_horario_w	:= nr_sequencia_p;
	end if;
 
	if (coalesce(nr_seq_menor_horario_w,0) <> 0) then 
		select	max(ds_horarios) 
		into STRICT	ds_horarios_w 
		from	prescr_material 
		where	nr_prescricao	= nr_prescricao_p 
		and	nr_sequencia	= nr_seq_menor_horario_w;
		 
		if (coalesce(ds_horario_composto_p,'X') <> 'X') then --Voltar todos os itens ao horário original. 
			ds_horarios_w	:= ds_horario_composto_p;
		end if;
		 
		update	prescr_material 
		set	ds_horarios	= ds_horarios_w, 
			nm_usuario	= nm_usuario_p, 
			dt_atualizacao	= clock_timestamp() 
		where	nr_prescricao	= nr_prescricao_p 
		and	((ie_volta_horario_composto_p = 'S' AND nr_agrupamento = nr_agrup_ant_p) or (nr_sequencia		= nr_sequencia_p)) 
		and	nr_sequencia		<> nr_seq_menor_horario_w 
		and	obter_classif_material_proced(cd_material, null,null) <> 1 
		and	coalesce(nr_sequencia_solucao::text, '') = '' 
		and	coalesce(nr_sequencia_proc::text, '') = '' 
		and	ie_agrupador		= 1 
		and	ie_suspenso		<> 'S' 
		and	coalesce(ie_administrar,'S')	= 'S' 
		and	coalesce(nr_sequencia_diluicao::text, '') = '';
 
		ds_horarios_p	:= ds_horarios_w;
		 
		commit;
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_horarios_agrup_medic ( nr_prescricao_p bigint, nr_sequencia_p bigint, nr_agrupamento_p bigint, nr_agrup_ant_p bigint, ie_farm_enf_p text, ie_volta_horario_composto_p text, ds_horario_composto_p text, ds_horarios_p INOUT text, cd_estabelecimento_p bigint, ie_volta_horario_p text, nm_usuario_p text) FROM PUBLIC;

