SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_horas_cronograma_npi ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_total_w		double precision;
nr_seq_cronograma_w	bigint;
dt_inicio_prev_w	timestamp;
dt_fim_prev_w		timestamp;


BEGIN

	select	max(nr_seq_cronograma)
	into STRICT	nr_seq_cronograma_w
	from	proj_cron_etapa
	where	nr_sequencia = nr_sequencia_p;

	if (nr_seq_cronograma_w > 0) then

		select	sum(coalesce(a.qt_hora_prev,0)),
			min(a.dt_inicio_prev),
			max(a.dt_fim_prev)
		into STRICT	qt_total_w,
			dt_inicio_prev_w,
			dt_fim_prev_w
		from	proj_cron_etapa a,
			proj_cronograma b
		where	b.nr_sequencia = a.nr_seq_cronograma
		and	b.nr_sequencia = nr_seq_cronograma_w;

		update	proj_cronograma
		set	qt_total_horas = qt_total_w,
			nm_usuario = nm_usuario_p,
			dt_inicio = dt_inicio_prev_w,
			dt_fim = dt_fim_prev_w
		where	nr_sequencia = nr_seq_cronograma_w;

	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
