SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_horas_etapa_cron (nr_seq_etapa_p bigint) AS $body$
DECLARE


qt_hora_prev_sup_w		double precision;
qt_hora_real_sup_w		double precision;
qt_horas_etapa_adic_w 	double precision;
nr_seq_superior_w		bigint;
nr_seq_superior_sup_w		bigint;
lista_superior_w		bigint;
qt_hora_rat_w			double precision;
qt_equipe_w			bigint;


BEGIN

if (nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '') then
	begin
	select	max(a.nr_seq_superior)
	into STRICT	nr_seq_superior_w
	from	proj_cron_etapa a
	where	a.nr_sequencia	= nr_seq_etapa_p;

	lista_superior_w	:= nr_seq_superior_w;
	while (lista_superior_w IS NOT NULL AND lista_superior_w::text <> '') loop
	begin

		select	coalesce(sum(a.qt_hora_prev),0),
			coalesce(sum(a.qt_hora_real),0),
			coalesce(sum(qt_horas_etapa_adic),0)
		into STRICT	qt_hora_prev_sup_w,
			qt_hora_real_sup_w,
			qt_horas_etapa_adic_w
		from	proj_cron_etapa a
		where	a.nr_seq_superior = lista_superior_w
		and (verifica_equipe_cronograma(a.nr_sequencia) = 'T')
		and	coalesce(a.ie_situacao,'A') = 'A';
		
		select	coalesce(sum(dividir(qt_min_ativ, 60)),0)
		into STRICT	qt_hora_rat_w
		from 	proj_rat_ativ
		where 	nr_seq_etapa_cron = lista_superior_w;
		
		update	proj_cron_etapa
		set	qt_hora_prev	= qt_hora_prev_sup_w,
			qt_hora_real	= qt_hora_rat_w + qt_hora_real_sup_w,
			qt_horas_etapa_adic = qt_horas_etapa_adic_w
		where	nr_sequencia	= lista_superior_w;

		select	a.nr_seq_superior
		into STRICT	nr_seq_superior_sup_w
		from	proj_cron_etapa a
		where	a.nr_sequencia	= lista_superior_w;

		lista_superior_w		:= null;
		if (nr_seq_superior_sup_w IS NOT NULL AND nr_seq_superior_sup_w::text <> '') then
			lista_superior_w	:= nr_seq_superior_sup_w;
		end if;
	end;
	end loop;
	end;	
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
