SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_horas_prescr_medica ( nr_prescricao_p bigint, nr_horas_p bigint ) AS $body$
DECLARE

qt_existe_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_existe_w
from 	prescr_medica
where 	nr_prescricao = nr_prescricao_p;

if (qt_existe_w > 0) then

	update 	prescr_medica
	set 	nr_horas_validade 	= nr_horas_p
	where 	nr_prescricao 	= nr_prescricao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
