CREATE OR REPLACE PROCEDURE atualizar_hora_aplic_prescr ( nr_prescricao_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN

if	(nr_prescricao_p > 0 AND nr_sequencia_p > 0) then
	update	prescr_material
	set	qt_min_aplicacao	 = NULL,
		qt_hora_aplicacao	 = NULL
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia		= nr_sequencia_p
	and	ie_aplic_bolus		= 'N'
	and	ie_aplic_lenta		= 'N';

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
