CREATE OR REPLACE PROCEDURE atualizar_hr_valid_prescr_med ( dt_prim_horario_p timestamp, dt_prescricao_p timestamp, nr_atendimento_p bigint, nr_prescricao_p bigint, ie_estender_p text, ie_regra_prim_prescr_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_horas_validade_w	double precision;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	nr_horas_validade_w	:= obter_horas_validade_prescr( 
					dt_prim_horario_p, 
					nr_atendimento_p, 
					ie_estender_p, 
					ie_regra_prim_prescr_p, 
					dt_prescricao_p, 
					nr_prescricao_p);
	 
	if (nr_horas_validade_w IS NOT NULL AND nr_horas_validade_w::text <> '') then 
		begin 
		update	prescr_medica 
		set	nr_horas_validade	= nr_horas_validade_w, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_prescricao		= nr_prescricao_p;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
