CREATE OR REPLACE PROCEDURE atualizar_ie_adep_prescr_medic ( nr_prescricao_p bigint, cd_setor_atendimento_p bigint, ident_prescr_adep_p text, nm_usuario_p text) AS $body$
DECLARE


ie_adep_w	varchar(1) := 'S';

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ident_prescr_adep_p IS NOT NULL AND ident_prescr_adep_p::text <> '') then
	begin
	if (ident_prescr_adep_p = 'DS') and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then
		begin
		select	CASE WHEN ie_adep='P' THEN 'N'  ELSE ie_adep END
		into STRICT	ie_adep_w
		from	setor_atendimento
		where	cd_setor_atendimento = cd_setor_atendimento_p;
		end;
	elsif (ident_prescr_adep_p = 'NV') or (ident_prescr_adep_p = 'PNV') then
		begin
		ie_adep_w := 'N';
		end;
	elsif (ident_prescr_adep_p = 'PPN') then
		begin
		ie_adep_w := '';
		end;
	end if;

	update	prescr_medica
	set	ie_adep		= ie_adep_w,
		nm_usuario 	= nm_usuario_p,
		dt_atualizacao 	= clock_timestamp()
	where	nr_prescricao 	= nr_prescricao_p;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
