CREATE OR REPLACE PROCEDURE atualizar_ie_kit_gerado ( ie_kit_gerado_p text, nr_prescricao_p bigint, nr_seq_item_p bigint, nm_tabela_p text) AS $body$
BEGIN

if (nm_tabela_p	= 'PRESCR_PROCEDIMENTO') then

	update	prescr_procedimento
	set	ie_kit_gerado	= ie_kit_gerado_p
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_item_p;

elsif (nm_tabela_p	= 'PRESCR_MATERIAL') then

	update	prescr_material
	set	ie_kit_gerado	= ie_kit_gerado_p
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_item_p;

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
