SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_impressao_requisicao ( nr_sequencia_p bigint) AS $body$
DECLARE


qt_itens_w	bigint;

BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	count(1)
	into STRICT	qt_itens_w
	from	cm_requisicao_item
	where	nr_seq_requisicao = nr_sequencia_p;

	if (qt_itens_w > 0) then
		update	cm_requisicao
		set	dt_impressao = clock_timestamp()
		where	nr_sequencia = nr_sequencia_p
		and	qt_itens_w > 0;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
