CREATE OR REPLACE PROCEDURE atualizar_imp_lote_aqui_java ( nr_sequencia_p bigint, nm_maquina_atual_p text, nm_usuario_p text) AS $body$
DECLARE



ds_maq_user_w 		varchar(80);
cd_perfil_ativo_w		integer;


BEGIN
if (coalesce(nm_maquina_atual_p,'') <> '') then
	begin
	ds_maq_user_w := nm_maquina_atual_p;
	end;
else
	begin
	select	substr(obter_inf_sessao(0) ||' - ' || obter_inf_sessao(1),1,80)
	into STRICT	ds_maq_user_w
	;
	end;
end if;

cd_perfil_ativo_w	:= obter_perfil_ativo;

update	ap_lote
set	dt_impressao_aqui	= clock_timestamp(),
	nm_usuario_imp_aqui	= nm_usuario_p,
	ds_maquina_imp_aqui	= ds_maq_user_w,
	cd_perfil_imp_aqui	= cd_perfil_ativo_w
where	nr_sequencia		= nr_sequencia_p;

insert into ap_lote_historico(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_lote,
		ds_evento,
		ds_log)
	values (	nextval('ap_lote_historico_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_p,
		wheb_mensagem_pck.get_texto(245156),
		wheb_mensagem_pck.get_texto(311639));

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
