CREATE OR REPLACE PROCEDURE atualizar_incluso_carta ( CD_PESSOA_FISICA_P text, CD_MEDICO_P text, NM_USUARIO_P text, IE_INCLUSO_P text, NR_ATENDIMENTO_P bigint) AS $body$
BEGIN
	if (IE_INCLUSO_P = 'S') then
		update	carta_conteudo
		set		IE_INCLUSO_CARTA = ie_incluso_p
		where	NR_SEQ_CARTA = obter_carta_edicao(cd_pessoa_fisica_p, cd_medico_p, nm_usuario_p, nr_atendimento_p)
		and		coalesce(IE_INCLUSO_CARTA::text, '') = '';
	elsif (IE_INCLUSO_P = 'N') then
		delete	from	carta_conteudo
		where	NR_SEQ_CARTA = obter_carta_edicao(cd_pessoa_fisica_p, cd_medico_p, nm_usuario_p, nr_atendimento_p)
		and		coalesce(IE_INCLUSO_CARTA::text, '') = '';
	end if;
	commit;

end;
$body$
