CREATE OR REPLACE PROCEDURE atualizar_inconsistencias_ret (nr_sequencias_p text) AS $body$
BEGIN

	update	conv_retorno_validacao
	set	IE_VALIDA_RETORNO = 'S'
	where	nr_sequencia in (WITH RECURSIVE cte AS (

				SELECT (regexp_substr(nr_sequencias_p,'[^,]+', 1, level))::numeric  nr_sequencia
				 
				(regexp_substr(nr_sequencias_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencias_p, '[^,]+', 1, level))::text <> '')
				  UNION ALL

				SELECT (regexp_substr(nr_sequencias_p,'[^,]+', 1, level))::numeric  nr_sequencia
				 
				(regexp_substr(nr_sequencias_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencias_p, '[^,]+', 1, level))::text <> '')
				 JOIN cte c ON ()

) SELECT * FROM cte;
);
	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
