SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_indicador_clinico ( dt_referencia_p timestamp, nr_seq_indicador_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_atendimento_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_atendimento 
	from	atendimento_paciente 
	where	dt_entrada	between trunc(dt_referencia_p,'month') and last_day(trunc(dt_referencia_p,'dd')) + 86399/86400 
	order by 1;


BEGIN 
 
OPEN C01;
LOOP 
FETCH C01 into 
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	CALL gerar_indic_clinico_atend(nr_atendimento_w, nr_seq_indicador_p, nm_usuario_p);
END LOOP;
CLOSE C01;
commit;
 
CALL EIS_Gerar_Indic_Clinico(dt_referencia_p, nm_usuario_p);
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
