CREATE OR REPLACE PROCEDURE atualizar_ind_niss_mensal ( dt_referencia_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE

 
 
dt_atual_w	timestamp;
dt_final_w	timestamp;


BEGIN 
 
dt_atual_w	:= PKG_DATE_UTILS.start_of(dt_referencia_p, 'month', 0);
dt_final_w	:= PKG_DATE_UTILS.get_datetime(PKG_DATE_UTILS.end_of(dt_referencia_p, 'MONTH', 0), dt_referencia_p);
 
while	((dt_atual_w <= dt_final_w) and (dt_atual_w <= clock_timestamp())) loop 
	begin 
 
	CALL atualizar_indicador_niss(dt_atual_w, nm_usuario_p, cd_estabelecimento_p);
 
	dt_atual_w	:= dt_atual_w + 1;
 
	end;
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
