CREATE OR REPLACE PROCEDURE atualizar_informacoes_titulo ( nr_titulo_p bigint, ie_opcao_p text, ds_valor_p text, vl_valor_p bigint, dt_valor_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
