BEGIN
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	if (ie_opcao_p = 'SJ') then
		update	titulo_receber
		set	vl_saldo_juros	= vl_valor_p
		where	nr_titulo	= nr_titulo_p;
	elsif (ie_opcao_p = 'SM') then
		update	titulo_receber
		set	vl_saldo_multa	= vl_valor_p
		where	nr_titulo	= nr_titulo_p;
	end if;
end if;

commit;

end;
