CREATE OR REPLACE PROCEDURE atualizar_info_import301 ( dt_ultima_exec_p timestamp, qt_tempo_interval_p bigint) AS $body$
DECLARE


nr_sequencia_w		c301_regra_scheduler.nr_sequencia%type;
dt_proxima_exec_w 	c301_regra_scheduler.dt_proxima_exec_import%type;

total_minutos_dia_w bigint;


BEGIN

select (24 * 60) minutos_dia
into STRICT 	total_minutos_dia_w
;

select	nr_sequencia
into STRICT	nr_sequencia_w
from	c301_regra_scheduler;

select  	clock_timestamp() + (qt_tempo_interval_p / total_minutos_dia_w) dt_proxima_exec
into STRICT	dt_proxima_exec_w
;

update	c301_regra_scheduler
set	dt_ultima_exec_import = dt_ultima_exec_p,
	dt_proxima_exec_import = dt_proxima_exec_w
where	nr_sequencia = nr_sequencia_w;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
