SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_inf_copia_prescr ( nr_prescricao_p bigint, ds_lista_atualizar_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_lista_atual_w	varchar(4000);
ds_lista_secundaria_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_pos_hifem_w		bigint;
nr_seq_item_w		bigint;
nr_ctrl_loop_w		smallint := 0;
ie_alterar_dose_w	varchar(1);
qt_dose_w		double precision;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ds_lista_atualizar_p IS NOT NULL AND ds_lista_atualizar_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	ds_lista_atual_w	:= ds_lista_atualizar_p;
	 
	while(ds_lista_atual_w IS NOT NULL AND ds_lista_atual_w::text <> '') and (nr_ctrl_loop_w < 100) loop 
		begin 
		nr_ctrl_loop_w 		:= nr_ctrl_loop_w + 1;
		nr_pos_virgula_w	:= position(',' in ds_lista_atual_w);
		 
		if (coalesce(nr_pos_virgula_w,0) > 0) then 
			begin 
			ds_lista_secundaria_w	:= substr(ds_lista_atual_w,0,nr_pos_virgula_w-1);
			ds_lista_atual_w	:= substr(ds_lista_atual_w,nr_pos_virgula_w+1,length(ds_lista_atual_w));
			end;
		else 
			begin 
			ds_lista_secundaria_w	:= ds_lista_atual_w;
			ds_lista_atual_w	:= null;
			end;
		end if;
		 
		if (ds_lista_secundaria_w IS NOT NULL AND ds_lista_secundaria_w::text <> '') then 
			begin 
			nr_pos_hifem_w	:= position('-' in ds_lista_secundaria_w);
		 
			if (coalesce(nr_pos_hifem_w,0) > 0) then 
				begin 
				 
				nr_seq_item_w		:= substr(ds_lista_secundaria_w,0,nr_pos_hifem_w-1);
				ds_lista_secundaria_w	:= substr(ds_lista_secundaria_w,nr_pos_hifem_w+1,length(ds_lista_secundaria_w));
 
				if (ds_lista_secundaria_w IS NOT NULL AND ds_lista_secundaria_w::text <> '') then 
					begin 
					nr_pos_hifem_w	:= position('-' in ds_lista_secundaria_w);
		 
					if (coalesce(nr_pos_hifem_w,0) > 0) then 
						begin 
						ie_alterar_dose_w	:= substr(ds_lista_secundaria_w,0,nr_pos_hifem_w-1);
						ds_lista_secundaria_w	:= substr(ds_lista_secundaria_w,nr_pos_hifem_w+1,length(ds_lista_secundaria_w));
						qt_dose_w		:= replace(substr(ds_lista_secundaria_w,0,length(ds_lista_secundaria_w)),'.',',');
						ds_lista_secundaria_w	:= null;
						end;
					end if;
					 
					if (nr_seq_item_w IS NOT NULL AND nr_seq_item_w::text <> '') and (nr_seq_item_w > 0) and (coalesce(ie_alterar_dose_w,'N') = 'S') then 
						begin 
						CALL alterar_dose_medic( 
							nr_prescricao_p, 
							nr_seq_item_w, 
							qt_dose_w, 
							wheb_usuario_pck.get_cd_estabelecimento, 
							wheb_usuario_pck.get_cd_perfil, 
							nm_usuario_p);
						end;
					end if;
					end;
				end if;	
				end;
			else 
				begin 
				ds_lista_secundaria_w	:= null;
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
