SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_inicio_final_checkup ( nr_atendimento_p bigint, cd_medico_p text, ie_opcao_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_checkup_w	bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		b.nr_seq_checkup
	from	checkup_etapa b,
		checkup a
	where	a.nr_sequencia		= b.nr_seq_checkup
	and	a.nr_atendimento	= nr_atendimento_p
	and	b.cd_pessoa_fisica	= cd_medico_p
	and	coalesce(b.dt_inicio_real::text, '') = ''
	and     (a.dt_inicio_real IS NOT NULL AND a.dt_inicio_real::text <> '')
	and     coalesce(a.dt_fim_real::text, '') = ''
	order by b.dt_prevista desc;

C02 CURSOR FOR
	SELECT	b.nr_sequencia,
		b.nr_seq_checkup
	from	checkup_etapa b,
		checkup a
	where	a.nr_sequencia		= b.nr_seq_checkup
	and	a.nr_atendimento	= nr_atendimento_p
	and	b.cd_pessoa_fisica	= cd_medico_p
	and	(b.dt_inicio_real IS NOT NULL AND b.dt_inicio_real::text <> '')
	and	coalesce(b.dt_fim_etapa::text, '') = ''
	and     (a.dt_inicio_real IS NOT NULL AND a.dt_inicio_real::text <> '')
	and     coalesce(a.dt_fim_real::text, '') = ''
	order by b.dt_prevista desc;


BEGIN

if (ie_opcao_p = 'I') then
	open	c01;
	loop
	fetch	c01 into
		nr_sequencia_w,
		nr_seq_checkup_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_sequencia_w		:= nr_sequencia_w;
		nr_seq_checkup_w	:= nr_seq_checkup_w;
		end;
	end loop;
	close c01;

	update	checkup_etapa
	set	dt_inicio_real 	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_w
	and	nr_seq_checkup	= nr_seq_checkup_w;
else
	open	c02;
	loop
	fetch	c02 into
		nr_sequencia_w,
		nr_seq_checkup_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		nr_sequencia_w		:= nr_sequencia_w;
		nr_seq_checkup_w	:= nr_seq_checkup_w;
		end;
	end loop;
	close c02;

	update	checkup_etapa
	set	dt_fim_etapa	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_w
	and	nr_seq_checkup	= nr_seq_checkup_w;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
