SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_inicio_separacao_qt (nr_seq_requisicao_p bigint) AS $body$
BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then        
        
        update  can_ordem_prod c
        set     c.dt_inicio_separacao = clock_timestamp()
        where   c.nr_sequencia in (
                SELECT  a.nr_seq_ordem_prod
                from    requisicao_ordem_prod a,
                        requisicao_material b
                where    a.nr_requisicao = b.nr_requisicao
                and      a.nr_requisicao = nr_seq_requisicao_p
                and      (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> ''));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
