SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_inicio_transcricao ( nr_transcricao_p bigint, nm_usuario_p text, ie_iniciar_transcr_p text) AS $body$
DECLARE


nr_prescr_w	   prescr_medica.nr_prescricao%type;
cd_funcao_w	   funcao.cd_funcao%type;


BEGIN
if (nr_transcricao_p IS NOT NULL AND nr_transcricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (ie_iniciar_transcr_p IS NOT NULL AND ie_iniciar_transcr_p::text <> '') then

	cd_funcao_w		:= coalesce(obter_funcao_ativa,0);

	select coalesce(max(nr_prescricao),0)
	into STRICT  nr_prescr_w
	from  prescr_medica
	where nr_seq_transcricao = 	nr_transcricao_p;

	if (nr_prescr_w <> 0) or (ie_iniciar_transcr_p = 'N') or (cd_funcao_w <> 1700) then

		update	transcricao_prescricao
		set	nm_usuario_transcricao	= CASE WHEN ie_iniciar_transcr_p='S' THEN  nm_usuario_p  ELSE null END ,
			dt_transcricao		    = CASE WHEN ie_iniciar_transcr_p='S' THEN  clock_timestamp()  ELSE null END
		where	nr_sequencia		= nr_transcricao_p;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
