CREATE OR REPLACE PROCEDURE atualizar_item_cot_fornec ( nr_cot_compra_p bigint, nr_seq_fornec_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
ds_fornecedor_w		varchar(255);
nr_sequencia_w		bigint;

c00 CURSOR FOR 
SELECT	nr_sequencia 
from	cot_compra_forn 
where	nr_cot_compra	= nr_cot_compra_p 
and	nr_sequencia	= nr_seq_fornec_p 
and	ie_opcao_p	= 0 

union all
 
SELECT	nr_sequencia 
from	cot_compra_forn 
where	nr_cot_compra	= nr_cot_compra_p 
and	ie_opcao_p	= 1;


BEGIN 
open C00;
loop 
fetch C00 into	 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C00 */
	begin	 
	CALL gerar_cot_compra_forn_item(nr_cot_compra_p,nr_sequencia_w,nm_usuario_p,'N');
	 
	select	substr(obter_nome_pf_pj(cd_pessoa_fisica, cd_cgc_fornecedor),1,255) 
	into STRICT	ds_fornecedor_w 
	from	cot_compra_forn 
	where	nr_sequencia = nr_sequencia_w;
	 
	CALL gerar_hist_cotacao_sem_commit( 
		nr_cot_compra_p, 
		WHEB_MENSAGEM_PCK.get_texto(297625), 
		substr(	WHEB_MENSAGEM_PCK.get_texto(297626,'DS_FORNECEDOR_W=' || ds_fornecedor_w),1,4000), 
		'S', 
		nm_usuario_p);
	 
	 
	 
	end;
end loop;
close C00;
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
