CREATE OR REPLACE PROCEDURE atualizar_item_mat_entrada ( vl_unitario_item_nf_p bigint, nr_item_nf_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_total_item_nf_w	nota_fiscal_item.vl_total_item_nf%type;
vl_desconto_w		nota_fiscal_item.vl_desconto%type;
qt_item_nf_w		nota_fiscal_item.qt_item_nf%type;
vl_liquido_w		nota_fiscal_item.vl_liquido%type;
pr_desconto_w		nota_fiscal_item.pr_desconto%type;


BEGIN

select	vl_desconto,
		qt_item_nf
into STRICT		vl_desconto_w,
		qt_item_nf_w
from		nota_fiscal_item
where	nr_sequencia = nr_sequencia_p
and		nr_item_nf = nr_item_nf_p;

vl_total_item_nf_w := (vl_unitario_item_nf_p * qt_item_nf_w);
vl_liquido_w := (vl_total_item_nf_w - vl_desconto_w);

if (vl_total_item_nf_w <> 0) then
	pr_desconto_w := (vl_desconto_w*100)/vl_total_item_nf_w;
else
	pr_desconto_w := (vl_desconto_w*100)/1;
end if;


update	nota_fiscal_item
set		vl_unitario_item_nf = vl_unitario_item_nf_p,
		vl_total_item_nf = vl_total_item_nf_w,
		vl_liquido = vl_liquido_w,
		pr_desconto = pr_desconto_w
where	nr_sequencia = nr_sequencia_p
and		nr_item_nf = nr_item_nf_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
