CREATE OR REPLACE PROCEDURE atualizar_item_selec_nf (ie_selecionado_p text, nr_seq_nota_p bigint, nr_seq_item_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_acao_p = 'S') then

	update	nota_fiscal_item
	set	ie_selecionado = ie_selecionado_p
	where	nr_sequencia = nr_seq_nota_p
	and	nr_item_nf = nr_seq_item_p;

elsif (ie_acao_p = 'F')	then

	update	nota_fiscal_item
	set	ie_selecionado = 'N'
	where	ie_selecionado = 'S';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
