SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_justificativa_prescr ( nr_prescricao_p bigint, ds_justificativa_p text ) AS $body$
DECLARE

qt_existe_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_existe_w
from 	prescr_medica
where 	nr_prescricao = nr_prescricao_p;

if (qt_existe_w > 0) then
	update	prescr_medica
	set 	ds_justificativa 	= substr(ds_justificativa_p,1,2000)
	where 	nr_prescricao 	= nr_prescricao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
