CREATE OR REPLACE PROCEDURE atualizar_leito_chamado_manut ( nm_usuario_p text, cd_unidade_basica_p text, cd_unidade_compl_p text, cd_setor_atendimento_p bigint, ie_opcao_p text, nr_sequencia_p bigint default null) AS $body$
DECLARE


/* C chamado manutencao
*/
			


BEGIN

if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') and (cd_unidade_basica_p IS NOT NULL AND cd_unidade_basica_p::text <> '') and (cd_unidade_compl_p IS NOT NULL AND cd_unidade_compl_p::text <> '') then
	
	if (ie_opcao_p = 'C') then

		update	unidade_atendimento
		set	ie_status_unidade    = 'C',
			nm_usuario           = nm_usuario_p,
			dt_atualizacao       = clock_timestamp()
		where	cd_unidade_basica    = cd_unidade_basica_p
		and	cd_unidade_compl     = cd_unidade_compl_p
		and 	cd_setor_atendimento = cd_setor_atendimento_p;
		
		if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
			CALL inserir_hist_gestao_servico('RM', nr_sequencia_p, nm_usuario_p);
		end if;
	end if;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
