CREATE OR REPLACE PROCEDURE atualizar_leito_manutencao ( nm_usuario_p text, cd_unidade_basica_p text, cd_unidade_compl_p text, cd_setor_atendimento_p bigint, ie_opcao_p text, ie_trigger_p text default 'N') AS $body$
DECLARE

			 
/* M manutenção 
  L libera Livre 
  G Libera higienização   
*/
 
 
nr_seq_interno_w 	bigint;
ie_considera_hig_w	varchar(1);
ie_higienizacao_w	varchar(1);
cd_paciente_reserva_w	varchar(10);


BEGIN 
 
select max(cd_paciente_reserva) 
into STRICT	cd_paciente_reserva_w 
from	unidade_atendimento 
where	cd_setor_atendimento 	= cd_setor_atendimento_p 
and	cd_unidade_basica  	= cd_unidade_basica_p 
and	cd_unidade_compl 	= cd_unidade_compl_p;
 
ie_considera_hig_w := Obter_param_Usuario(44, 234, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_considera_hig_w);
 
if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') and (cd_unidade_basica_p IS NOT NULL AND cd_unidade_basica_p::text <> '') and (cd_unidade_compl_p IS NOT NULL AND cd_unidade_compl_p::text <> '') then 
	 
	if (ie_opcao_p = 'M') then 
 
		update	unidade_atendimento 
		set	ie_status_unidade  = 'E', 
			nm_usuario      = nm_usuario_p, 
			dt_atualizacao    = clock_timestamp() 
		where	cd_unidade_basica  = cd_unidade_basica_p 
		and	cd_unidade_compl   = cd_unidade_compl_p 
		and 	cd_setor_atendimento = cd_setor_atendimento_p;
 
	else	 
 
		if (ie_considera_hig_w = 'S') then 
		 
			select	max(ie_higienizacao)		 
			into STRICT	ie_higienizacao_w 
			from	unidade_atendimento 
			where	cd_setor_atendimento = cd_setor_atendimento_p 
			and	cd_unidade_compl   = cd_unidade_compl_p 
			and 	cd_unidade_basica  = cd_unidade_basica_p;
			 
			if (ie_higienizacao_w = 'N') then 
			 
				update	unidade_atendimento 
				set	ie_status_unidade  = CASE WHEN cd_paciente_reserva_w = NULL THEN  'L'  ELSE 'R' END , 
					nm_usuario      = nm_usuario_p, 
					dt_atualizacao    = clock_timestamp(), 
					cd_motivo_manutencao  = NULL 
				where	cd_unidade_basica  = cd_unidade_basica_p 
				and	cd_unidade_compl   = cd_unidade_compl_p 
				and 	cd_setor_atendimento = cd_setor_atendimento_p;
			 
			elsif (ie_higienizacao_w = 'H') then 
				 
				update	unidade_atendimento 
				set	ie_status_unidade  = 'G', 
					nm_usuario      = nm_usuario_p, 
					dt_atualizacao    = clock_timestamp(), 
					cd_motivo_manutencao  = NULL 
				where	cd_unidade_basica  = cd_unidade_basica_p 
				and	cd_unidade_compl   = cd_unidade_compl_p 
				and 	cd_setor_atendimento = cd_setor_atendimento_p;
 
 
				select nr_seq_interno 
				into STRICT nr_seq_interno_w 
				from unidade_atendimento 
				where	cd_unidade_basica  = cd_unidade_basica_p 
				and	cd_unidade_compl   = cd_unidade_compl_p 
				and 	cd_setor_atendimento = cd_setor_atendimento_p;
 
				CALL gerar_higienizacao_leito_unid(clock_timestamp(), nm_usuario_p,wheb_usuario_pck.get_cd_estabelecimento, 'FM', nr_seq_interno_w, null);		
			end if;
			 
		end if;	
		if (ie_considera_hig_w = 'N') or 
			(ie_considera_hig_w = 'S' AND ie_higienizacao_w = 'A') then 
			 
			if (ie_opcao_p = 'L') then 
			 
				update	unidade_atendimento 
				set	ie_status_unidade  = CASE WHEN cd_paciente_reserva_w = NULL THEN  'L'  ELSE 'R' END , 
					nm_usuario      = nm_usuario_p, 
					dt_atualizacao    = clock_timestamp(), 
					cd_motivo_manutencao  = NULL 
				where	cd_unidade_basica  = cd_unidade_basica_p 
				and	cd_unidade_compl   = cd_unidade_compl_p 
				and 	cd_setor_atendimento = cd_setor_atendimento_p;
			 
			elsif (ie_opcao_p = 'G') then 
				 
				update	unidade_atendimento 
				set	ie_status_unidade  = 'G', 
					nm_usuario      = nm_usuario_p, 
					dt_atualizacao    = clock_timestamp(), 
					cd_motivo_manutencao  = NULL 
				where	cd_unidade_basica  = cd_unidade_basica_p 
				and	cd_unidade_compl   = cd_unidade_compl_p 
				and 	cd_setor_atendimento = cd_setor_atendimento_p;
 
 
				select nr_seq_interno 
				into STRICT nr_seq_interno_w 
				from unidade_atendimento 
				where	cd_unidade_basica  = cd_unidade_basica_p 
				and	cd_unidade_compl   = cd_unidade_compl_p 
				and 	cd_setor_atendimento = cd_setor_atendimento_p;
 
				CALL gerar_higienizacao_leito_unid(clock_timestamp(), nm_usuario_p,wheb_usuario_pck.get_cd_estabelecimento, 'FM', nr_seq_interno_w, null);		
			end if;
		end if;
	end if;
end if;
 
if (ie_trigger_p = 'N') then 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
