SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_liberacao_brasindice (nr_sequencia_p bigint, ie_desfazer_p text, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	update	brasindice_preco
	set	dt_liberacao = CASE WHEN ie_desfazer_p='S' THEN  null  ELSE clock_timestamp() END ,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
